temp = flow.get("temp");
target = flow.get("target");
delta = flow.get("delta");
var bypass = {payload: 0}; 
var deplag = {payload: 0};

if (temp >= (target + delta)) {
    bypass.payload = 0;
    deplag.payload = 0;
} else if (temp >= target) {
    bypass.payload = 0;
    deplag.payload = 0;
} else if (temp < target && temp >= (target - delta)) {
    bypass.payload = 1;
    deplag.payload = 0;
} else {
    bypass.payload = 1;
    deplag.payload = 1;
}

return [bypass, deplag];

// Applies a simple RC low pass filter to incoming payload values
var tc = 30*1000;       // time constant in milliseconds

var lastValue = context.get('lastValue');
if (typeof lastValue == "undefined") lastValue = msg.payload;
var lastTime = context.get('lastTime') || null;
var now = new Date();
var currentValue = msg.payload;
if (lastTime === null) {
    // first time through
    newValue = currentValue;
} else {
    var dt = now - lastTime;
    var newValue;

    if (dt > 0) {
        var dtotc = dt / tc;
        newValue = lastValue * (1 - dtotc) + currentValue * dtotc;
    } else {
        // no time has elapsed leave output the same as last time
        newValue = lastValue;
    }
}
context.set('lastValue', newValue);
context.set('lastTime', now);

msg.payload = newValue + 20;
return msg;

